import java.lang.String;
import java.lang.StringBuilder;
import static org.junit.Assert.assertEquals;
import org.junit.Test;

public class SherlockBeast {

  public static void main(String[] args) {
    SherlockBeastTest tests = new SherlockBeastTest();
    tests.runTests();
  }

  String getDecentNumber(int nOfDigits) {
    StringBuilder stringBuilder = new StringBuilder();
    for (int i = nOfDigits; i >= 0; i--) {
      if (i % 3 == 0 && (nOfDigits - i) % 5 == 0) {
        int j;
        for (j = 0; j < i; j++)
          stringBuilder.append("5");
        for (int k = j; k < nOfDigits; k++)
          stringBuilder.append("3");
        break;
      }
    }
    if (stringBuilder.length() == 0)
      return ("-1");
    else
      return stringBuilder.toString();
  }
}

class SherlockBeastTest {
  @Test
  void runTests() {
    SherlockBeast sherlockBeast = new SherlockBeast();

    assertEquals("-1", sherlockBeast.getDecentNumber(1));
    assertEquals("555", sherlockBeast.getDecentNumber(3));
    assertEquals("33333", sherlockBeast.getDecentNumber(5));
    assertEquals("55555533333", sherlockBeast.getDecentNumber(11));
    assertEquals("", sherlockBeast.getDecentNumber(2194));
  }
}
